    private void CRActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CRActionPerformed
        Double celcius = Double.parseDouble(String.valueOf(tfCelcius.getText()));
        Double reamur = celcius * 0.8;
        tfHCelcius.setText(String.valueOf(reamur) + " ° Ré");
    }//GEN-LAST:event_CRActionPerformed

    private void CKActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CKActionPerformed
        Double celcius = Double.parseDouble(String.valueOf(tfCelcius.getText()));
        Double kelvin = celcius + 273.15;
        tfHCelcius.setText(String.valueOf(kelvin) + " ° K");
    }//GEN-LAST:event_CKActionPerformed

    private void HCelciusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HCelciusActionPerformed
        tfCelcius.setText(null);
        tfHCelcius.setText(null);
    }//GEN-LAST:event_HCelciusActionPerformed

    private void CFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CFActionPerformed
        Double celcius = Double.parseDouble(String.valueOf(tfCelcius.getText()));
        Double fahrenheit = celcius * 1.8 + 32;
        tfHCelcius.setText(String.valueOf(fahrenheit) + " ° F");
    }//GEN-LAST:event_CFActionPerformed

    private void KRActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_KRActionPerformed
        Double kelvin = Double.parseDouble(String.valueOf(tfKelvin.getText()));
        Double reamur = (kelvin - 273.15) * 0.8;
        tfHKelvin.setText(String.valueOf(reamur) + " ° Ré");
    }//GEN-LAST:event_KRActionPerformed

    private void KCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_KCActionPerformed
        Double kelvin = Double.parseDouble(String.valueOf(tfKelvin.getText()));
        Double celcius = kelvin - 273.15;
        tfHKelvin.setText(String.valueOf(celcius) + " ° C");
    }//GEN-LAST:event_KCActionPerformed

    private void HKelvinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HKelvinActionPerformed
        tfKelvin.setText(null);
        tfHKelvin.setText(null);
    }//GEN-LAST:event_HKelvinActionPerformed

    private void KFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_KFActionPerformed
        Double kelvin = Double.parseDouble(String.valueOf(tfKelvin.getText()));
        Double fahrenheit = kelvin * 1.8 - 459.67;
        tfHKelvin.setText(String.valueOf(fahrenheit) + " ° F");
    }//GEN-LAST:event_KFActionPerformed

    private void FRActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FRActionPerformed
        Double fahrenheit = Double.parseDouble(String.valueOf(tfFahrenheit.getText()));
        Double reamur = (fahrenheit - 32) / 2.25;
        tfHFahrenheit.setText(String.valueOf(reamur) + " ° Ré");
    }//GEN-LAST:event_FRActionPerformed

    private void FCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FCActionPerformed
        Double fahrenheit = Double.parseDouble(String.valueOf(tfFahrenheit.getText()));
        Double celcius = (fahrenheit - 32) / 1.8;
        tfHFahrenheit.setText(String.valueOf(celcius) + " ° C");
    }//GEN-LAST:event_FCActionPerformed

