    private void FKActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FKActionPerformed
        Double fahrenheit = Double.parseDouble(String.valueOf(tfFahrenheit.getText()));
        Double kelvin = (fahrenheit + 459.67) / 1.8;
        tfHFahrenheit.setText(String.valueOf(kelvin) + " ° K");
    }//GEN-LAST:event_FKActionPerformed

    private void RKActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RKActionPerformed
        Double reamur=Double.parseDouble(String.valueOf(tfReamur.getText()));
        Double kelvin=reamur/0.8+273.15;
        tfHReamur.setText(String.valueOf(kelvin) + " ° K");
    }//GEN-LAST:event_RKActionPerformed

    private void RCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RCActionPerformed
        Double reamur=Double.parseDouble(String.valueOf(tfReamur.getText()));
        Double celcius=reamur/0.8;
        tfHReamur.setText(String.valueOf(celcius) + " ° C");
    }//GEN-LAST:event_RCActionPerformed

    private void HReamurActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HReamurActionPerformed
        tfReamur.setText(null);
        tfHReamur.setText(null);
    }//GEN-LAST:event_HReamurActionPerformed

    private void RFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RFActionPerformed
        Double reamur=Double.parseDouble(String.valueOf(tfReamur.getText()));
        Double fahrenheit=reamur*2.25+32;
        tfHReamur.setText(String.valueOf(fahrenheit) + " ° F");
    }//GEN-LAST:event_RFActionPerformed

    private void HFahrenheitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HFahrenheitActionPerformed
        tfFahrenheit.setText(null);
        tfHFahrenheit.setText(null);
    }//GEN-LAST:event_HFahrenheitActionPerformed

